    public class Day19 : Day
    {
        public Day19() : base(19)
        {
        }

        protected override void RunDay()
        {
            WritePartTitle();
            var finder = new TubeRouteFinder(Input);
            finder.FindRoute();
            Console.WriteLine($"Letters found: {finder.Route}");

            WritePartTitle();
            Console.WriteLine($"Step count: {finder.StepCount}");
        }

